import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
export const userSlice = createSlice({
    name: "user",
    initialState: {
        username: "",
        email: "",
        isFetching: false,
        isSuccess: false,
        isError: false,
        errorMessage: "",
    },
    reducers: {
        // Reducer comes here
    },
    extraReducers: {
        // Extra reducer comes here
    },
})
export const userSelector = state => state.user
